import React, { Component } from 'react';
import axios from 'axios';
//import { confirmAlert } from 'react-confirm-alert';
//import 'react-confirm-alert/src/react-confirm-alert.css';

import { Link } from 'react-router-dom';

export default class PropAtelier extends Component {

    constructor(props) {
        super(props);
        this.state = { profil: [] };

    }


    componentDidMount() {
        axios.get(`http://localhost:8080/api/users/newEleve`)
            .then(response => {
                console.log('user-article ==== ', response.data)
                this.setState({ profil: response.data });
            })
            .catch(function (error) {
                console.log(error);
            })

    }

    liste() {
        return <div id="eleve">

        <div className="container-fluid" >

            <div className="row view-group">
                {
                    (this.state.profil.length > 0) ? (this.state.profil.map((obj) => {

                        return <div key={obj._id} className="item col-xs-4 col-lg-4 card" id="suivie">


                            <center >
                                <img width="200px" height="140px" src={'http://localhost:8080/api/users/newEleveImage/' + obj.image} alt="pdp" />  
                            </center>

                            <p>
                                <Link to={"/modifierEleve/" + obj._id}  class="btn btn-success">Modifier</Link>
                                <button  class="btn btn-danger" onClick={(e)=>{
                                   // e.preventefault()
                                    axios.delete("http://localhost:8080/api/users/delete/"+ obj._id)
                                    .then(
                                        console.log("suppression avec succes"),
                                        console.log(obj._id)
                                        )
                                    .catch(error => console.log(error))
                                }}>supprimer</button>
                            </p>

                        </div>

                    })) : ('')
                }
            </div>
        </div>
       </div>
    }
    render() {
        return (
            <div className='app'>
                {this.liste()}
            </div>
        );
    }
}
